mapper.select:
select line_id, line_name, line_link, line_image, priority, enable_status, create_time, last_edit_time from tb_head_line
and enable_status = #{headLineCondition.enableStatus}
order by priority desc

mapper.update:
update tb_head_line
line_name = #{lineName}, line_link = #{lineLink}, line_image = #{lineImage}, priority = #{priority}, enable_status = #{enableStatus}, last_edit_time = #{lastEditTime}
where line_id = #{lineId}
