mapper.update:
update tb_product
product_name = #{productName}, product_description = #{productDescription}, image_address = #{imageAddress}, normal_price = #{normalPrice}, promotion_price = #{promotionPrice}, priority = #{priority}, point = #{point}, enable_status = #{enableStatus},
product_category_id = #{productCategory.productCategoryId}
where product_id = #{productId} and shop_id = #{shop.shopId};

mapper.select:
select product_id, product_name, product_description, image_address, normal_price, promotion_price, priority, point, create_time, last_edit_time, enable_status, shop_id, product_category_id from tb_product
and shop_id = #{productCondition.shop.shopId}
and product_category_id = #{productCondition.productCategory.productCategoryId}
and product_name like '%${productCondition.productName}%'
and enable_status = #{productCondition.enableStatus}
order by priority desc limit #{rowIndex},#{pageSize};
