mapper.select:
select shop_category_id, shop_category_name, shop_category_image, shop_category_description, priority, create_time, last_edit_time, parent_id from tb_shop_category
and parent_id is null
and parent_id is not null
and parent_id = #{shopCategoryCondition.parent.shopCategoryId}
order by priority desc;

mapper.update:
update tb_shop_category
shop_category_name=#{shopCategoryName}, shop_category_description=#{shopCategoryDescription}, shop_category_image=#{shopCategoryImage}, priority=#{priority}, last_edit_time=#{lastEditTime}, parent_id=#{parent.shopCategoryId}
where shop_category_id=#{shopCategoryId}
