mapper.select:
select s.shop_id, s.shop_name, s.shop_description, s.shop_address, s.phone, s.shop_image, s.priority, s.create_time, s.last_edit_time, s.enable_status, s.advice, a.area_id, a.area_name, sc.shop_category_id, sc.shop_category_name FROM tb_shop s, tb_area a, tb_shop_category sc
and s.shop_category_id = #{shopCondition.shopCategory.shopCategoryId}
and s.shop_category_id in ( select shop_category_id from tb_shop_category where parent_id = #{shopCondition.shopCategory.parent.shopCategoryId} )
and s.area_id = #{shopCondition.area.areaId}
and s.shop_name like '%${shopCondition.shopName}'
and s.enable_status= #{shopCondition.enableStatus}
and s.owner_id = #{shopCondition.user.userId}
and s.area_id = a.area_id and s.shop_category_id = sc.shop_category_id
order by priority desc limit #{rowIndex},#{pageSize};

mapper.update:
update tb_shop
area_id = #{area.areaId}, shop_category_id = #{shopCategory.shopCategoryId}, shop_name = #{shopName}, shop_description = #{shopDescription}, shop_address = #{shopAddress}, phone = #{phone}, shop_image = #{shopImage}, priority = #{priority}, last_edit_time = #{lastEditTime}, enable_status = #{enableStatus}, advice = #{advice}
where shop_id = #{shopId};

update
tb_shop
set
enable_status = #{enableStatus}
where
shop_id = #{shopId}
